import React, { Component } from "react";
import PropTypes from "prop-types";
import { connect } from "react-redux";
import { fetchUserRepos } from "../actions/postActions";
import { Button, Input } from "../styles";
import Table, {
  TableHead,
  TableBody,
  TableRow,
  TableCell
} from "@kiwicom/orbit-components/lib/Table";

class SearchRepos extends Component {
  constructor(props) {
    super(props);
    this.state = {
      username: ""
    };
    this.onChange = this.onChange.bind(this);
    this.onSubmit = this.onSubmit.bind(this);
    this.truncate = this.truncate.bind(this);
  }

  componentWillMount(username) {
    //   const val = 'priyanka-joshi'
    //   this.props.fetchUserRepos(val);
  }

  onChange(e) {
    this.setState({ username: e.target.value });
  }

  onSubmit(e) {
    e.preventDefault();
    this.props.fetchUserRepos(this.state.username);
  }

  truncate(string){
    if (string.length > 50)
      return string.substring(0,5)+'...';
    else
      return string;
  };

  render() {
    const repoItems = this.props.repos
      ? this.props.repos.map(repo => (
          <TableRow>
            <div key={repo.id}>
              <TableCell>
                <h3>{repo.name}</h3>
              </TableCell>
              <TableCell align="center">
                  {repo.description
                    ? this.truncate(repo.description)
                    : "Repository description does not exist"}
              </TableCell>
            </div>
          </TableRow>
        ))
      : "Username does not exist";

    console.log(repoItems);

    return (
      <React.Fragment>
        <div>
          <h1>Get Github Repositories</h1>
          <form onSubmit={this.onSubmit}>
            <div>
              <label> Enter the github username </label>
              <Input
                type="text"
                name="username"
                value={this.state.username}
                onChange={this.onChange}
              />
              <Button type="submit">Submit</Button>
            </div>
          </form>
        </div>
        <div>
          {repoItems.length
            ? `We found the following ${
                repoItems.length
              } repositories for this user`
            : ""}
          {repoItems.length ? <Table>
            <TableHead>
              <TableRow>
                <TableCell>Name</TableCell>
                <TableCell>Description</TableCell>
              </TableRow>
            </TableHead>
          </Table> : '' }   
          <Table>{repoItems}</Table>
        </div>
      </React.Fragment>
    );
  }
}

SearchRepos.prototypes = {
  fetchUserRepos: PropTypes.func.isRequired,
  repos: PropTypes.array.isRequired
};

const mapStateToProps = state => ({
  repos: state.posts.repos
});

export default connect(
  mapStateToProps,
  { fetchUserRepos }
)(SearchRepos);
