import React, { Component } from "react";
import PropTypes from "prop-types";
import { connect } from "react-redux";
import { fetchUserRepos } from "../actions/postActions";

class SearchRepos extends Component {
  constructor(props){
    super(props);
    this.state = {
      title : '',
      body : ''
    }
    this.onChange = this.onChange.bind(this);
    this.onSubmit = this.onSubmit.bind(this);

  }

  componentWillMount(username) {
  //   const val = 'priyanka-joshi'
  //   this.props.fetchUserRepos(val);
   }

   onChange(e){
    this.setState({[e.target.name] : e.target.value})
  }  

  onSubmit(e) {
    e.preventDefault();
    // const post = {
    //   title: this.state.title,
    //   body: this.state.body
    // };
    const username = e.target.value
    this.props.fetchUserRepos(username);
  }

  render() {
    const repoList = this.props.repos;
    console.log(repoList);
    const repoItems = this.props.repos.map(repo => (
      <div key ={repo.id}>
        <h3>{repo.name}</h3>
        <p>{repo.description}</p>
      </div>
    ));
    return (
      <div>
        <form>
          <div>
            <label> Enter the user name </label>
            <input type="text" value="username" />
          </div>
        </form>
        <div>
          {repoItems}
        </div>
      </div>
    );
  }
}

SearchRepos.prototypes = {
  fetchUserRepos : PropTypes.func.isRequired,
  repos : PropTypes.array.isRequired
}


const mapStateToProps = state => ({
    repos : state.posts.repos
  });


export default connect(mapStateToProps, { fetchUserRepos })(SearchRepos);
